mapper.insert:
INSERT INTO MEMBER ( MEMBER_ID, EMAIL, NICKNAME, MEMBER_NAME, PASSWORD, ZIPCODE, ROAD_ADDRESS, DETAIL_ADDRESS, GENDER, BIRTHDAY, PHONE_NUMBER
, PROFILE_URL
, CREATED_AT, CREATED_BY, UPDATED_AT, UPDATED_BY ) VALUES ( SEQ_MEMBER.NEXTVAL, #{email}, #{nickName}, #{memberName}, #{passwordHash}, #{zipcode}, #{roadAddress}, #{detailAddress}, #{gender}, #{birthday, jdbcType=DATE}, #{phoneNumber}
, #{profileUrl}
, SYSTIMESTAMP, 0, SYSTIMESTAMP, 0 )

mapper.select:
SELECT CASE WHEN COUNT(1) > 0 THEN 1 ELSE 0 END
FROM MEMBER
WHERE EMAIL = #{email}

SELECT CASE WHEN COUNT(1) > 0 THEN 1 ELSE 0 END
FROM MEMBER
WHERE NICKNAME = #{nickname}
