mapper.select:
SELECT COUNT(*)
FROM HEALTH_INFO
WHERE MEMBER_ID = #{memberId}

mapper.insert:
INSERT INTO HEALTH_INFO (
    MEMBER_ID, STEPS, TOTAL_CALORIES_BURNED, BLOOD_GLUCOSE,
    BLOOD_PRESSURE, NUTRITION, SLEEPSESSION,
    CREATED_AT, CREATED_BY, UPDATED_AT, UPDATED_BY
) VALUES (
             #{memberId}, #{req.steps}, #{req.totalKcal}, #{req.bloodGlucoseMgdl},
             NULL, #{req.intakeKcal}, #{req.sleepMinutes},
             SYSTIMESTAMP, #{memberId}, SYSTIMESTAMP, #{memberId}
         )
